package collections;

import java.util.*;

public class MinAndMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		List<Integer> la = new ArrayList<>();
		la.add(10);
		la.add(-1);
		la.add(6);
		System.out.println("Min: " + Collections.min(la));
		System.out.println("Max: " + Collections.max(la));
	}

}
